import React, { useState, useEffect } from 'react'
import championsImage from "./championslol.jpg"
import "./style.css"
import { db } from './firebase-config'
import { collection, getDocs } from 'firebase/firestore';


function App() {

 const [showMenu, setShowMenu] = useState(false);
 const [xPos, setXPos] = useState(0);
 const [yPos, setYPos] = useState(0);
 const [yordles, setYordles] = useState([]);
 const [currArea, setCurrArea] = useState(null)
 const yordlesCollectionRef = collection(db, "yordles")

 useEffect(() => {
  const getYordles = async () => {
    const data = await getDocs(yordlesCollectionRef);
    setYordles(data.docs.map((doc) => ({...doc.data(),  id: doc.id})))
  }

  getYordles();

 }, []);


useEffect(() => {
  function handleClick(e) {
    if(!showMenu) {
    setShowMenu(!showMenu)
    setXPos(e.pageX)
    setYPos(e.pageY)
    console.log(yordles)
      console.log(xPos, yPos, "yordle coords", yordles[2].coordX, yordles[2].coordY)
    } else if (showMenu) {
      setShowMenu(!showMenu)
    }

  }

   document.addEventListener('click', handleClick)

   return function cleanupListener() {
    document.removeEventListener('click', handleClick)
  }

 },[showMenu, setShowMenu])

 function handleArea(e) {
  setCurrArea(e.target.id)
 }

/* function checkValidity() {
    yordles.forEach((yordle) => {
      if(currArea === yordle.name && yordle.found) {
        setCurrArea(null)
        return alert(yordle.name + " already found")
      } 
      if (currArea === yordle.name) {
        alert(yordle.name + " found")
        yordle.found = true;
        setCurrArea(null);
        console.log(yordles)
      }
    })
 } */

  function checkValidity() {
    yordles.forEach((yordle) => {
      if((xPos >= yordle.coordX && yPos >= yordle.coordY)|| xPos+2 <= yordle.coordX && yPos+2 <= yordle.coordY  ) {
        alert(yordle.name + "found")
      }
    })
  }

 function dropDownMenu() {

  return (
    <div>
    <div className="targetBox" style={{ left: xPos-25, top: yPos-25}}></div>
      <ul className="yordleList" style={{ left: xPos+54, top: yPos-45}}>
        <li onClick={() => checkValidity()}>Teemo</li>
        <li onClick={() => checkValidity()}>Tristana</li>
        <li onClick={() => checkValidity()}>Poppy</li>
      </ul>
    </div>
  )
}

  return (
    <div className="App">

      {showMenu ? dropDownMenu() : null}
      <img className="lolChampions" src={championsImage} alt="lolChampions" useMap="#lolMap" width="900" height="500"></img>
      <map name="lolMap">
      <area alt="teemo" id="Teemo" coords="525,447,571,503" shape="rect" onClick={(e) => handleArea(e) }/>
     <area alt="tristana" id="Tristana" coords="537,238,578,200" shape="rect" onClick={(e) => handleArea(e)}/>
     <area alt="poppy" id="Poppy" coords="250,436,189,504" shape="rect" onClick={(e) => handleArea(e)}/>
       </map>

        </div>
  );
}

export default App;
