import React, { useState, useEffect } from 'react'
import championsImage from "./championslol.jpg"
import "./style.css"
import { db } from './firebase-config'
import { collection, getDocs } from 'firebase/firestore';


function App() {

 const [showMenu, setShowMenu] = useState(false);
 const [xPos, setXPos] = useState(0);
 const [yPos, setYPos] = useState(0);
 const [coords, setCoords] = useState([]);
 const [currArea, setCurrArea] = useState(null)
 const coordsCollectionRef = collection(db, "yordleCoords")

 useEffect(() => {
  const getCoords = async () => {
    const data = await getDocs(coordsCollectionRef);
    setCoords(data.docs.map((doc) => ({...doc.data(),  id: doc.id})))
    console.log(coords[0].name)
  }

  getCoords();

 }, []);


useEffect(() => {
  function handleClick(e) {
    if(!showMenu) {
    setShowMenu(!showMenu)
    setXPos(e.pageX)
    setYPos(e.pageY)

    } else if (showMenu) {
      setShowMenu(!showMenu)
    }

  }

   document.addEventListener('click', handleClick)

   return function cleanupListener() {
    document.removeEventListener('click', handleClick)
  }

 },[showMenu, setShowMenu])

 function handleArea(e) {
  setCurrArea(e.target.id)
  console.log(e.target.id)
 }

 function checkValidity() {
    coords.forEach((yordle) => {
      if(currArea === yordle.name) {
        alert(yordle.name + " found")
        yordle.found = true;
        setCurrArea(null)
      }
    })
 }

 function dropDownMenu() {

  return (
    <div>
    <div className="targetBox" style={{ left: xPos-25, top: yPos-25}}></div>
      <ul className="yordleList" style={{ left: xPos+54, top: yPos-45}}>
        <li onClick={() => checkValidity()}>Teemo</li>
        <li >Tristana</li>
        <li >Poppy</li>
      </ul>
    </div>
  )
}

  return (
    <div className="App">

      {showMenu ? dropDownMenu() : null}
      <img className="lolChampions" src={championsImage} alt="lolChampions" useMap="#lolMap" width="900" height="500"></img>
      <map name="lolMap">
      <area alt="teemo" id="Teemo" coords="525,447,571,503" shape="rect" onClick={(e) => handleArea(e) }/>
     <area alt="tristana" id="Tristana" coords="537,238,578,200" shape="rect"/>
     <area alt="poppy" id="Poppy" coords="250,436,189,504" shape="rect"/>
       </map>
        </div>
  );
}

export default App;
