import React, { useState, useEffect } from "react";
import { doc, setDoc, Timestamp, updateDoc, getDocs, getDoc, collection } from "firebase/firestore"; 
import { db } from './firebase-config';

function Timer( {gameOver} ) {
    const [time, setTime] = useState(0);
    const [timerOn, setTimerOn] = useState(true);
    const scoresRef = collection(db, 'scores');
    const [newScores, setNewScores] = useState([])
    const [highscore, setHighscore] = useState(0)


 async function saveDataStart() {
    await updateDoc(scoresRef, {
        startTime: Math.floor(Date.now()/1000),
      });
    }  


  async function saveDataEnd() {
    await updateDoc(scoresRef, { endTime: Math.floor(Date.now()/1000) });
  }

    useEffect(() => {
            const getScores = async () => {
                const dataScores = await getDocs(scoresRef);
                setNewScores(dataScores.docs.map((doc) => ({...doc.data(),  id: doc.id})))


        }
        getScores()

    },[])


    useEffect(() => {
        let interval = null;

        if(gameOver === true) {
            setTimerOn(false)
            saveDataEnd()

        }

        if(timerOn && gameOver === false) {
          interval = setInterval(() => {
            setTime(prevTime => prevTime + 10)
          },10)
          saveDataStart()


        } else {
            clearInterval(interval)
        }

        return () => clearInterval(interval)

    }, [timerOn, gameOver])



    return (
        <div className="timerContainer">
            <div>
            <span>{("0" + Math.floor((time / 60000) % 60)).slice(-2)}:</span>
            <span>{("0" + Math.floor((time / 1000) % 60)).slice(-2)}:</span>
            <span>{("0" + ((time / 10) % 100)).slice(-2)}</span>
            <div>{newScores[0]}</div>
            </div>
        </div>
    )
}


export default Timer
